import React, { useState } from "react";

const   PLUS = (a,b) => a + b;
const MINUS = (a,b) => a - b;
const  MULRIPLY = (a,b) => a * b;
const RESET = 0;

function Calculator({a, b}) {
    const [operator, setOperator] = useState(() => PLUS);
    return(
        <main>
            <h1>Calculator</h1>
            <button
                onClick={() => setOperator(() => RESET)}
            >
                RESET
            </button>
            <button
                onClick={() => setOperator(() => PLUS)}
            >
                PLUS
            </button>
            <button
                onClick={() => setOperator(() => MINUS)}
            >
                MINUS
            </button>
            <button
                onClick={() => setOperator(() => MULRIPLY)}
            >
                MULRIPLY
            </button>
            <p>
                Result of applying operator to {a} and {b}:
                <code>{operator(a, b)}</code>
            </p>
        </main>
    );
}

function App() {
    return <Calculator a={7} b={4}/>
}

