import { useState } from "react";

function Counter({isVisible}) {
    if(!isVisible){
        return null;
    }
    const [counter, setCounter] = useState(0);
    return (
        <main>
            <p>Counter: {counter}</p>
            <button onClick={() => setCounter((value) => value + 1)}>
                Increment
            </button>
        </main>
    );
}



